import react from "react";

export default function Main(){

    const [isGoodTonight, setIsGoodTonight] = react.useState(true)

    function changeState(){
        // this function to change the state based on the previous(old) one (if true => return false) and vise versa
        setIsGoodTonight((prevState) =>{
            return prevState? false: true
        })
    }

    const [factsArray, editFactsArray] = react.useState(["Fact 1","Fact 2"])

    function addFact(){
        editFactsArray(prevArray => {
            return [...prevArray, `Fact ${prevArray.length + 1}`]
        })
    }

    const Facts = factsArray.map(fact => <li key={fact}>{fact}</li>)

    const [cardsData, setCardsData] = react.useState({
        firstName: "Nermeen",
        lastName: "Moustafa",
        position: "Front-End Developer"
    })

    return(
        <main>
            <div className="state" onClick={changeState}>
                <h1>Do I Feel Good Tonight? (click to toggle the answer from yes to no)</h1>
                <h2>{isGoodTonight?"Yes":"No"}</h2>
            </div>

            <div>
                <h1 className="Facts-header">Fun Facts about React</h1>
                <button className="addFactButton" onClick={addFact}>Add Fact</button>
                <ul className="Facts-list">
                    {Facts}
                </ul>
            </div>
            
            <article className="card">
                <h2>{cardsData.firstName} {cardsData.lastName}</h2>
                <h2 style={{marginLeft:"10px"}}>{cardsData.position}</h2>
                <button className="togglePositionButton">Toggle Position</button>
            </article>
        </main>
    )
}
