import React from "react";
import Die from "../components/Die"

export default function Game(){

    function randomDice(){
        const arr=[]
        for(let i=0 ; i<10 ; i++){
            arr.push(Math.ceil(Math.random()*6))
        }
        return arr
    }

    const[dices, setDices] = React.useState(randomDice())

    return(
        <div className="Game">
            <h1 className="title">Tenzies</h1>
            <h2 className="paragraph">Roll until all dice are the same.Click each die to freeze it 
                at its current value between rolls.</h2>

            <div className="dies">
                <Die value={1} />
                <Die value={5} />
                <Die value={3} />
                <Die value={4} />
                <Die value={2} />
                <br/>
                <Die value={6} />
                <Die value={5} />
                <Die value={2} />
                <Die value={4} />
                <Die value={3} />
            </div>

            <button className="rollButton">Roll</button>

        </div>
    )
}
