import react, { useState } from "react";
import ToggleButton from "../ToggleButton";
import boxes from "../Boxes"
import Box from "./Box";

export default function Main(){

    const [isGoodTonight, setIsGoodTonight] = react.useState(true)

    function changeState(){
        // this function to change the state based on the previous(old) one (if true => return false) and vise versa
        setIsGoodTonight((prevState) =>{
            return prevState? false: true
        })
    }

    const [factsArray, editFactsArray] = react.useState(["Fact 1","Fact 2"])

    function addFact(){
        editFactsArray(prevArray => {
            return [...prevArray, `Fact ${prevArray.length + 1}`]
        })
    }

    const Facts = factsArray.map(fact => <li key={fact}>{fact}</li>)

    const [cardsData, setCardsData] = react.useState({
        firstName: "Nermeen",
        lastName: "Moustafa",
        frontPosition: "Front-End Developer",
        backPosition: "Back-End Developer",
        isFrontEnd: true
    })

    function togglePosition(){

        setCardsData(prev => {
            if(prev.isFrontEnd)
                // ...prev to get the rest of the object before edit then edit then update the old object with the new one
                return{...prev, isFrontEnd:false}
            else
                return{...prev, isFrontEnd:true}              
        })

    }

    const [squares, setSquares] = react.useState(boxes)

    function squareToggle(id){
        
        setSquares(prev => {
            return prev.map(square => {
                return square.id === id ? {...square, on : ! square.on} : square
            })
        })
    }

    const squareElements = squares.map(square => (
        <Box    key={square.id} 
                on={square.on} 
                toggle={() => squareToggle(square.id)}/>
     ) 
    )

    const [formData, setFormData] = react.useState(
        {
            firstName : "",
            lastName : "",
            email : "",
            isKoki : false,
            comments : "",
            employmentStatus : "",
            favColor: ""
        }
    )

    function eventHandler(event){

        const{name, value, type, checked} = event.target

        setFormData(prev => {
            return {
                ...prev,
                [name] : type === "checkbox" ? checked : value
            }
        })
    }

    function submitHandler(event){
        event.preventDefault()
        console.log(formData)
    }

    //use effect functio is used to run something based on the change of another thing
    //for example here this console.log will run each time the boolean value of "isKoki" will change
    react.useEffect(() => {
        console.log("Effect function ran")
    }, [formData.isKoki])

    const [starWarsData, setStarWarsData] = useState({})

    //here as i want this api call to happen once, i put an empty array in the dependency array parameter
    // if i put [0] or any static value that doesn't change that will be the same
    react.useEffect(() =>{
        fetch("https://swapi.dev/api/people/1")
        .then(res => res.json())
        .then(data => setStarWarsData(data))
    }, [])

    return(
        <main>
            <div className="state" onClick={changeState}>
                <h1>Do I Feel Good Tonight? (click to toggle the answer from yes to no)</h1>
                <h2>{isGoodTonight?"Yes":"No"}</h2>
                {isGoodTonight && <h2>I feel Great, thanks</h2>}
            </div>

            <div>
                <h1 className="Facts-header">Fun Facts about React</h1>
                <button className="addFactButton" onClick={addFact}>Add Fact</button>
                <ul className="Facts-list">
                    {Facts}
                </ul>
            </div>
            
            <article className="card">
                <h2>{cardsData.firstName} {cardsData.lastName}</h2>
                <h2 style={{marginLeft:"50px"}}>{cardsData.isFrontEnd?cardsData.frontPosition:cardsData.backPosition}</h2>
                <ToggleButton handleClick={togglePosition}/>
            </article>

            <article>
                <h1 className="squaresHeader">click the squares to change their color</h1>
                {squareElements}
            </article>

            <form className="form" onSubmit={submitHandler}>
                <h1>Form</h1>

                <input type="text" placeholder="First Name" name="firstName" onChange={eventHandler} 
                        value={formData.firstName} />
                <input type="text" placeholder="Last Name" name="lastName" onChange={eventHandler}
                        value={formData.lastName} />
                <input type="email" placeholder="Email" name="email" onChange={eventHandler}
                        value={formData.email}/>

                <br/>

                <input type="checkbox" id="kokiCheck" checked={formData.isKoki} name="isKoki" onChange={eventHandler}/>
                <label htmlFor="kokiCheck">Is that you, koki ?</label>

                <br/>
                <textarea placeholder="Add your comment..." name="comments" onChange={eventHandler}
                         value={formData.comments}/>

                <h1>you Entered : {formData.firstName} {formData.lastName} {formData.email} {formData.comments}</h1>
               
                <fieldset className="fieldSet">
                    <legend>current employment status</legend>

                    <input type="radio" id="unemployed" name="employmentStatus" value="Unemployed" onChange={eventHandler} />
                    <label htmlFor="unemployed">Unemployed</label>
                    <br/>

                    <input type="radio" id="part-time" name="employmentStatus" value="Part-time" onChange={eventHandler} />
                    <label htmlFor="part-time">Part-time</label>
                    <br/>

                    <input type="radio" id="full-time" name="employmentStatus" value="Full-time" onChange={eventHandler} />
                    <label htmlFor="full-time">Full-time</label>

                    <p>you choosed : {formData.employmentStatus}</p>
              </fieldset>

                <br/>

                <article className="favColor">
                    <label htmlFor="favColor">what's your favourite color?</label>
                    <br/>
                    <select id="favColor" name="favColor" value={formData.favColor} onChange={eventHandler} >
                        <option value="">--choose--</option>
                        <option value="red">red</option>
                        <option value="green">green</option>
                        <option value="yellow">yellow</option>
                        <option value="blue">blue</option>
                    </select>
                    <p>you choosed : {formData.favColor}</p>
                </article>

                <button>Submit Form</button>

            </form>

            <pre>{JSON.stringify(starWarsData, null, 2)}</pre>
            

        </main>
    )
}
