import React from "react";
import Die from "../components/Die"

export default function Game(){

    function randomDice(){
        const arr=[]
        for(let i=0 ; i<10 ; i++){
            arr.push({
                value: Math.ceil(Math.random()*6),
                isHeld: false
            })
        }
        return arr
    }

    const[dices, setDices] = React.useState(randomDice())

    const dies = dices.map(die => {
        return <Die value={die.value}/>
    })

    function rollDice(){
        setDices(randomDice())
    }

    return(
        <div className="Game">
            <h1 className="title">Tenzies</h1>
            <h2 className="paragraph">Roll until all dice are the same.Click each die to freeze it 
                at its current value between rolls.</h2>

            <div className="dies">
                {dies}
            </div>

            <button className="rollButton" onClick={rollDice}>Roll</button>

        </div>
    )
}
